data.script:
var price = $price;
var is3min = (minute(time) % 3) === 0;
if (is3min) {
  // "navpična črta" = zelo ozek pas (širina 1 bara)
  // preko celotne višine grafa
  brokenarea(
    price.close - price.close,
    price.close + price.close,
    color = (options.markerColor || "rgba(255,255,255,0.35)")
  );
}
