data.script:
var theprice = avg_ohlc(bar)
volume = vsell + vbuy
averageVolume = sma(vsell, 10) + sma(vbuy, 10)
climax = volume*(theprice.high - theprice.low)

//highestClimax10 = Math.max((#climax).slice(-10))
highestClimax10 = highest(climax,10)

var climaxVolume = ((volume >= (2 * averageVolume)) | (climax >= highestClimax10))

var risingVolume = volume >= (1.5 * averageVolume) & !climaxVolume


if(climaxVolume)
{
  plotcandlestick({time: time, open: theprice.open, high: theprice.high, low: theprice.low, close: theprice.close},
  upColor=options.bullClimaxColor,     
  downColor=options.bearClimaxColor)
}
else if(risingVolume)
{
  plotcandlestick({time: time, open: theprice.open, high: theprice.high, low: theprice.low, close: theprice.close},
  upColor=options.bullRisingColor,     
  downColor=options.bearRisingColor)
}
else
{
  plotcandlestick({time: time, open: theprice.open, high: theprice.high, low: theprice.low, close: theprice.close},
  upColor=options.bullColor,     
  downColor=options.bearColor)
}
